std::string convertMarkdownToHTML(const std::string& text) {
    std::regex rx("```(.|\\n)*?```"); // capture "```...```" in the group 1.  The `\n` is required to make sure the next match will start at the beginning of the next line.
    std::string result;  // the string to return 
    std::sregex_token_iterator it(text.begin(), text.end(), rx, -1); // tokenize the text,  use '-1' as the flags,  because the function will skip leading spaces when not in a capture group.
    while (it != it.end()) { // while we still have characters in it...
        std::sregex_token_iterator next(it, it.end(), rx, -1); // get the next token and move on...  if we reach the end,  we'll get an empty string.  this is fine.  In that case,  we'll return an empty string as well.
        if (next == it) { // if we didn't get another token after that last one, then...  
            result += next->str(); // append that last token to our output string.  
            continue; // and go back to start of loop.  
        } else { // otherwise...  
            std::smatch match; // save the match for later use  
            if (std::regex_search(next->str(), match, rx)) { // see if this is a match for the regexp  
                result += "<p>"; // start a new paragraph tag  
                result += "    "; // add some space before the next line  
                result += match[1]; // get what was matched in group 1 (the blockquote), and prepend it with some space before it  
                result += "</p>\n"; // close off the paragraph tag  
