void convertMarkdownToHtml(std::string& input) {
  // Use the HTML parser to parse the Markdown.
  auto parser = std::make_shared<markdown::parser>();
  auto output = std::make_shared<markdown::output>();
  parser->parse(input, output);

  // Convert the HTML to a string.
  auto html = output->getHtml();
  html.append("<html>");
  html.append("<head>");
  html.append("<title>Converted Markdown</title>");
  html.append("</head>");
  html.append("<body>");
  html.append(html::to_string(html::escape_html(output->getHtml())));
  html.append("</body>");
  html.append("</html>");

  // Replace the Markdown with the HTML string.
  input = html;
}
