std::string convertMarkdownToHtml(const std::string& markdownString) {
    // create a new string for storing the converted html string
    std::string htmlString;

    // load the markdown text into a buffer
    std::string markdownText = markdownString;

    // remove any trailing newlines and replace them with a single newline character
    markdownText = markdownText.substr(0, markdownText.length() - 1);

    // split the markdown text into an array of strings by adding a newline character to the end of each line in the markdown text
    std::vector<std::string> lines = splitLines(markdownText);

    // convert each line in the array of strings to lowercase letters and remove any leading or trailing whitespace characters from each line
    for (const auto& line : lines) {
        line = line.substr(0, line.length());
        line = line.substr(0, line.find_first_of(" \t\n\r")); // trim left whitespace characters 
        line = line.substr(line.find_last_of(" \t\n\r") + 1); // trim right whitespace characters 
        line = line.lower(); // convert to lowercase letters 
        line = trimWhiteSpace(line); // remove leading and trailing whitespace characters 
    }

    // for each element in the array of strings, extract the link definition from the string, if present, and replace the link text with the content of the link definition 
    for (const auto& element : lines) { 
        if (isLinkDef(element)) { 
            element = extractLinkDefinition(element); 
            element = replaceLinkText(element); 
            element = trimWhiteSpace(element); 
        } 
    } 

    // join all of the elements in the array of strings into a single string by adding a newline character after each element in the array of strings 
    htmlString = joinLines(lines); 

    return htmlString; 
} 
