import java.util.Arrays;

public class Timsort {
  public static void sort(int[] array) {
    int length = array.length;
    int start = 0;
    int end = length - 1;
    while (start <= end) {
      int pivot = partition(array, start, end);
      if (pivot != -1) {
        swap(array, pivot, start);
        start++;
      } else {
        end--;
      }
    }
  }

  private static int partition(int[] array, int start, int end) {
    int low = start;
    int high = end - 1;
    int pivot = array[start];
    while (low <= high) {
      while (low <= high && array[high] >= pivot) {
        high--;
      }  // find left part of array that is smaller than pivot    
      while (low <= high && array[low] <= pivot) {       // find right part of array that is larger than pivot    																                                                  	                                                                  
        low++;                                             // swap low and high    				                                            if (low == high) { // swap low with array[high]     return low;   }       // swap low with array[high]     low++;       // swap low with array[high]     return high;   }       // swap low with array[high]     low++;       // swap low with array[high]     return high;   }       // swap low with array[high]     low++;       // swap low with array[high]     return high;   }       // swap low with array[high]     high--;   }           // find right part of array that is larger than pivot      if (low == high) {           // swap left and right          return low;         }           // swap left and right          low++;           // swap left and right          return high;         }           // swap left and right          low++;           // swap left and right          return high;         }           // swap left and right          return -1;         }  private static void swap(int[] a, int i, int j) { a[i] ^= a[j]; a[j] ^= a[i]; a[i] ^= a[j]; }```

