#include <iostream>
#include <fstream>
#include <string>
#include <cctype>

using namespace std;

int main() {
    ifstream myfile("text.txt");
    int wordCount = 0;
    string line;
    while (getline(myfile, line)) {
        for (auto c : line) {
            if (islower(c) && isalpha(c)) {
                if (isupper(c)) { // ignore upper case words 
                    continue;
                }
                if (c == ' ') { // ignore spaces between words
                    continue;
                } else if (c == word.at(0)) { // ignore the word we're counting 
                    wordCount++; // increment the word count 
                } else { // ignore other characters not in the word 
                    break;  // break out of the loop 
                } 
            } 
        } 
    } 
    myfile.close(); 
    cout << "The word " << word << " appears " << wordCount << " times in the text." << endl; 
}

