def longestCommonSubsequence(string1, string2):
    # Создадим пустой массив для хранения длины совпадений и массив сортированных слов
    lengths = []
    sorted_strings = sorted(string1 + string2)

    # Фильтруем массив сортированных слов, удаляя случайные символы
    for i in range(len(sorted_strings)):
        if sorted_strings[i] not in string1 and sorted_strings[i] not in string2:
            lengths.append(i)

    # Проходимся по массиву длин и определяем максимальную длину совпадения
    max_length = 0
    for i in lengths:
        if lengths[i] > max_length:
            max_length = lengths[i]

    return max_length, sorted_strings[:max_length]

