#include <std/algorithm>
#include <std/sort>

def sort_merge<T>(xs: seq[T], ys: seq[T]): seq[T] =
  let len = xs.size() + ys.size()
  if len <= 1:
    if len == 0: xs.empty
    else: ys.empty
  else:
    mid = len div 2
    lens = [xs[0..mid], ys[0..mid]]
    for i in mid + 1 .. len: lens[i] = [xs[i], ys[i]]
    var result = []
    for l, r in zip(lens, reverse(lens)): result.add(sort_merge(l, r))
    result
```

