def LSST(s1, s2):
    dp = {}
    n = len(s1)
    m = len(s2)

    for i in range(n):
        for j in range(m):
            if s1[i] == s2[j]:
                if i + 1 < n and s1[i + 1] == s2[j]:
                    dp[(i, j)] = dp.get((i, j), max(dp.get((i - 1, j), 0), dp.get((i, j - 1), 0), dp.get((i - 1, j - 1), 0))) + 1
                else:
                    dp[(i, j)] = dp.get((i, j), max(dp.get((i - 1, j), 0), dp.get((i, j - 1), 0))) + 1
                if i + 2 < n and s1[i + 2] == s2[j]:
                    dp[(i + 2, j)] = dp.get((i + 2, j), max(dp.get((i + 1, j), 0), dp.get((i + 2, j - 1), 0))) + 1
                else:
                    dp[(i + 2, j)] = dp.get((i + 2, j), max(dp.get((i + 1, j), 0), dp.get((i + 2, j - 1), 0))) + 1
            else:
                break
    return max(dp.values())
