std::string format_markdown_to_html(const std::string& markdown_str) {
    // Replace special characters with HTML entities
    std::replace(markdown_str.begin(), markdown_str.end(), '\n', "\\n");
    std::replace(markdown_str.begin(), markdown_str.end(), '\r', "\\r");
    std::replace(markdown_str.begin(), markdown_str.end(), '\t', "\\t");
    // Replace hashtags with HTML links
    std::replace(markdown_str.begin(), markdown_str.end(), "#", "<a href=\"#\" class=\"hashtag\" >#</a>");
    // Replace links with HTML links
    std::regex link_regex("\\[(.*?)](.*?)\\[/\\1\\]");
    std::smatch match;
    if (std::regex_match(markdown_str, match, link_regex)) {
        std::string link = match[1];
        std::string url = match[2];
        return "[" + link + "](<a href=\"" + url + "\" class=\"link\">" + url + "</a>)";
    } else {
        // Return the original string if there are no links in the text
        return markdown_str;
    }
}

