def longest_common_subsequence(a: str, b: str) -> int:
    a_size = len(a)
    b_size = len(b)
    a_d_p = [0] * (a_size + 1)
    b_d_p = [0] * (b_size + 1)
    for i in range(1, a_size + 1):
        for j in range(1, b_size + 1):
            if a[i-1] == b[j-1]:
                a_d_p[i] = max(a_d_p[i], i - a_d_p[i-1])
