    for i in range(3, n + 2): # for each element in dp table from index = 3 to index = n + 2
        dp[i][0] = dp[i - 1][0] + dp[i - 2][0] # initialize the first index of each row of dp table using the previous value of the first index of the row above it
        for j in range(1, i): # for each element in dp table from index = 1 to index = i - 1 except for the first two elements (index = 3 and index = 4)
