def find_largest_sequence(words, length):
    """Finds the longest common subsequence of two words.

    Args:
        words (list[str]): The words to compare.
        length (int): The maximum number of characters to search for in each word.
    """
    letters = set()
    for word in words:
        letters |= set(word)
    results = []
    for i in range(len(words[0])):
        for j in range(len(words[1])):
            if i + j >= length and i >= len(words[0]) - length and j >= len(words[1]) - length:
                break
            if words[0][i:i + j] in letters and words[1][j:j + i] in letters:
                results.append((i, j))
    return max(results, key=lambda x: x[0] * x[1])[0] * x[1]

