#include <iostream>
#include <string>
#include <regex>

std::string MarkdownToHtml(std::string const &markdown) {
    std::smatch matches; // The result of the regex pattern matching operation.
    std::regex re("([^\\n]+?)(\\n+)"); // The regex pattern for markdown tags.
    std::string output = ""; // The output string.
    std::regex_match(markdown, matches, re); // Matches markdown tags.
    for (int i = 1; i <= matches[2].length(); ++i) { // For each match of the regex pattern.
        output += "<span style=\"font-family: monospace;\">"; // Insert the start tag of the tag.
        if (matches[1][i - 1] == '"') { // For each quote tag.
            output += "&quot;"; // Insert the start tag with encoded quotes.
        } else {
            output += "<code>"; // Insert the start tag for code.
        }
        output += "&nbsp;&nbsp;" + matches[1][i]; // Insert the content of the tag.
        if (matches[1][i - 1] == '"') { // For each quote tag.
            output += "</code>"; // Insert the end tag for code.
        } else {
            output += "</span>"; // Insert the end tag of the tag.
        }
    }
    return output; // Return the html-formateret tekst.

