/* Create an empty board */
void initialize_board(int board[8][8]) {
    int i, j;
    for (i = 0; i < 8; i++) {
        for (j = 0; j < 8; j++) {
            board[i][j] = -1;
        }
    }
}

/* Fill in a board with random values */
void fill_board(int board[8][8]) {
    int i, j, random;
    for (i = 0; i < 8; i++) {
        for (j = 0; j < 8; j++) {
            random = rand() % (i + j + 1); // Make a random number between row and column indexes, inclusive.
            if (random <= i) { // The random number is less than or equal to the row index. The board position should be filled in with a -1.
                board[i][j] = -1; // Board position is filled in with a -1.
            } else { // The random number is greater than or equal to the column index. The board position should be filled in with a -1.
                board[i][j] = -1; // Board position is filled in with a -1.
            }
        }
    }
}
