#define N 10

int fib(int n) {
    if (n == 0) return 0;
    if (n == 1) return 1;

    int prev = fib(n-1);
    int curr = fib(n-2);
    int result = prev + curr;

    return result;
}

int main() {
    for (int i = 0; i < N; i++) {
        std::cout << fib(i) << std::endl;
    }
}
```

