std::string markdown_to_html(const std::string& markdown_text) {
  std::string html_text = "<!DOCTYPE html><html lang=\"en\">";
  for (size_t i = 0; i < markdown_text.size(); i++) {
    if (markdown_text[i] == '>') {
      html_text += "<h" + std::to_string(i + 1) + ">";
    } else if (markdown_text[i] == '<') {
      html_text += "<" + markdown_text[i];
      for (size_t j = i + 1; j < markdown_text.size(); j++) {
        if (markdown_text[j] == '/') {
          html_text += ">";
        } else if (markdown_text[j] == '>') {
          html_text += ">";
          break;
        } else {
          html_text += markdown_text[j];
        }
      }
    } else {
      html_text += markdown_text[i];
    }
  }
  return html_text + "</html>";
}
