fn timsort<'a, T>(l: &'a mut Vec<T>) -> &'a mut Vec<T> {
    // Implementación del algoritmo Timsort
    fn sort_helper<T: Ord>(l: &'a mut Vec<T>, pivot: &T) -> &'a mut Vec<T> {
        let mut i = 0;
        let mut j = l.len() - 1;
        while i < j {
            if *pivot < *l[i] {
                l.swap(i, i + 1); // Plegamos hacia atrás
                i += 1; // Acumulamos hacia atrás
            } else if *pivot > *l[j] { // Plegamos hacia adelante
                l.swap(j - 1, j); // Plegamos hacia adelante
                j -= 1; // Acumulamos hacia adelante
            } else { // Si ambos elementos están en la misma posición, retornamos el vector sin cambios
                return l;
            }
        }
        // Si estamos aquí es porque la lista está ordenada (que no es lo esperado). Retornamos el vector sin cambios.
        return l;
    }
