std::string convert_markdown_to_html(std::string& markdown) {
    // Split the markdown string into words.
    std::vector<std::string> words;
    std::string word;
    std::stringstream ss(markdown);
    while (getline(ss, word, ' ')) {
        words.push_back(word);
    }

