    while (in.good()) { // read lines from the file until EOF is reached
        getline(in, word); // read line from the file
        ++line_number; // increment line number for each line read from the file
        if (word == "The") { // if the word is "The", increment the counter by 1 and print it out on the screen and write it to the output file
