```python
from collections import deque

class Pile:
    def __init__(self, capacity=None):
        self.capacity = capacity or 0
        self.data = deque()

    def put(self, value):
        if len(self.data) == self.capacity:
            self.remove()
        self.data.append(value)

    def remove(self):
        self.data.popleft()

    def get(self):
        return self.data[-1]
