function tokenFunding(uint256 supply, uint256 amount) public {
    require(amount <= supply);
    require(amount > 0);
    require(supply > amount);
    uint256 remainingSupply = supply - amount;
    require(remainingSupply > 0);
    address[] memory _whitelistedAddresses = new address[](1);
    _whitelistedAddresses[0] = msg.sender;
    uint256[] memory _tokensToBeSold = new uint256[](1);
    _tokensToBeSold[0] = amount;
    emit TokenFunding(supply, amount, remainingSupply, _whitelistedAddresses, _tokensToBeSold);
}
```

