```c++
#include <string>
#include <iostream>
#include <sstream>
#include <regex>

std::string process_markdown_to_html(const std::string& text) {
    std::istringstream iss(text);
    std::stringstream html;
    std::regex markdown_regex("(?<!\[)(.*?)\\[([^]]+)\\](?![\\])");
    std::smatch match;
    while (iss >> match[1] >> match[2]) {
        std::regex::match_results<std::string::value_type> results = std::regex_search(match[1], markdown_regex);
        if (results.size() > 0) {
            // заменяем ссылку на тег и заменяем текст ссылки на html-код, который будет выводиться в браузере, вместо этой ссылки.
            // Это можно сделать так: [Hello, world!](https://www.google.com/) -> [Hello, world!](https://www.google.com/) -> <a href="https://www.google.com/">Hello, world!</a>.
            html << "<a href=\"" << results[2].first << "\">" << results[2].second << "</a>";
        } else {
            // если не удается найти соответствующий паттерн, то выводим содержимое ссылки в html-формате, как есть.
            html << match[1];
        }
    }
    return html.str();
}
```
