std::string convert_to_html(const std::string &line) {
    // Create a string stream to handle multiple lines of text as one input to the conversion function
    std::istringstream line_stream(line);

    // Split the line into words using "getline" and convert each word into HTML by adding it to the current HTML string
    for (std::string word; std::getline(line_stream, word, ' ');) {
        html += "<span class=\"text-" + word.substr(0, 1).lower() + "\" >" + word + "</span>";
