```C++
#include <iostream>
#include <string>
#include <sstream>

std::string convert(std::string input) {
    std::istringstream is(input);
    std::string output;
    std::string line;
    while (is >> line) {
        if (line == "#") {
            output += "<h1>";
        } else if (line == "##") {
            output += "<h2>";
        } else if (line == "###") {
            output += "<h3>";
        } else if (line == "####") {
            output += "<h4>";
        } else if (line == "#####") {
            output += "<h5>";
        } else if (line == "######") {
            output += "<h6>";
        } else {
            output += line;
        }
    }
    return output;
}
````

