def longest_common_subsequence(s1, s2):
    n1, m1 = len(s1), len(s2)
    dp = [[0] * m1 for _ in range(n1)]
    for i in range(n1):
        for j in range(m1):
            if s1[i] == s2[j]:
                dp[i][j] = 1 + dp[i-1][j-1]
