# Stacks queue class
class StacksQueue:
    def __init__(self):
        self.queue = []
        self.front = -1
        self.back = -1

    def enqueue(self, item):
        if self.back == len(self.queue) - 1:
            self.back = 0
            self.front = len(self.queue) - 1
        self.queue.append(item)
        self.back += 1
        return True # if item was added to the queue, return True, otherwise False

    def dequeue(self):
        if self.front == self.back: # if queue is empty, return None, otherwise return item at the front of the queue and move it to the back of the queue (to make it front again)
