def longest_common_subsequence(s1, s2):
    """Return the longest common subsequence between two strings.

    Args:
        s1 (str): String to be searched in.
        s2 (str): String to be searched in.
    Return:
        The length of the longest common subsequence between the two strings.
    """
    if len(s1) == 0:
        return len(s2)
    if len(s2) == 0:
        return len(s1)
    max_len = 0
    for i in range(len(s1)):
        sub_len = 0
