import sys

class ListNode(object):
    def __init__(self, value):
        self.value = value
        self.next = None

class LinkedList(object):
    def __init__(self):
        self.head = None
        self.tail = None
        self.length = 0

    def is_empty(self):
        return self.length == 0

    def push(self, value):
        node = ListNode(value)

        if self.head is None:
            self.head = node  # first node is head node and tail node
            self.tail = node  # first node is tail node and head node
            self.length += 1  # count length of list by one! (first list node is not counted)
        else:  # append new node at end of list (no need to reassign head, tail, etc)
            self.tail.next = node  # self.tail will be head node after insertion (tail will be empty)
