fn swap[T](xs: seq[T]): seq[T] {
  let (m, n) = xs.len / 2;
  xs[m] = xs[n];
  xs[n] = m;
  return xs;
}

// Fase de comparación
fn sort[T](xs: seq[T], less: T -> T -> bool): seq[T] {
  let (m, n) = xs.len / 2;
